mapper.update:
UPDATE BBS
   SET GROUP_NO = #{bbsNo}
 WHERE BBS_NO = #{bbsNo}

UPDATE BBS
   SET STATE = 0
 WHERE BBS_NO = #{bbsNo}

mapper.insert:

INSERT INTO BBS(
    WRITER
  , TITLE
  , IP
  , CREATED_AT
  , STATE
  , DEPTH
  , GROUP_NO
  , GROUP_ORDER
) VALUES (
    #{writer}
  , #{title}
  , #{ip}
  , NOW()
  , 1
  , #{depth}
  , #{groupNo}
  , #{groupOrder}
)
